// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

library EventStructs {
    enum PaymentCurrency { ETH, USD, BASE }
    enum EventTierType { FREE, GENERAL, PREMIUM }

    struct EventTier {
        uint256 price;
        uint256 maxQuantity;
        uint256 soldCount;
        bool isActive;
        PaymentCurrency currency;
    }

    struct EventSaleLimits {
        uint256 maxBatchSize;
        uint256 maxPerAddress;
        uint256 saleStartTime;
        uint256 saleEndTime;
    }

    struct PriceFeeds {
        address ethUsd;
        address baseUsd;
    }
}

